import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './guestbook.css';

function Guestbook() {
  const [posts, setPosts] = useState([])
  const [filename, setFilename] = useState('');
  const [file, setFile] = useState(null)
  const [subject, setSubject] = useState('')
  const [content, setContent] = useState('')

  useEffect(() => {
    window.scrollTo(0, 0)
    window.scrollTo = function () {};
  }, [])


  useEffect(() => {
    fetch('https://motluc-nammex.tlon.network/moo')
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log(data.content.split('ILOVEJOANBEAZ').filter((e: string) => e.includes('GUESTBOOK')));
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  }, []);

  return (
    <div id="chan" style={{overflow: "scroll" }}>
      <header style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
        <h1 id="title">33chan</h1>
        <p style={{width: '400px', textAlign: 'center'}}><i>an anonymous imageboard</i></p>
        <form onSubmit={async e=>{
            // upload to backend...
            e.preventDefault();
            if (file === null) {
              alert('Please include image...')
              return;
            }
            const formData = new FormData();
            formData.append('subject', subject);
            formData.append('content', content);
            if (file) {
              formData.append('file', file);
            }

            try {
              await axios.post(`http://${process.env.REACT_APP_BACKEND_URL}/posts`, formData, {
                headers: {
                  'Content-Type': 'multipart/form-data',
                },
              });
              setSubject('');
              setContent('');
              setFilename('');
              setFile(null);
              alert('Post created successfully!');
            } catch (error) {
              console.error('Error uploading post:', error);
              alert('Error uploading post!');
            }
            return false;
          }} style={{maxWidth: "fit-content"}}>
          <input name='subject' onChange={e=>setSubject(e.target.value)} value={subject} placeholder='Subject...' type="text"></input><br />
          <textarea name='content' onChange={e=>setContent(e.target.value)} value={content} placeholder='Say hi!' rows={3} cols={25}></textarea><br />
          <input name='file' style={{width: '230px'}} onChange={e=>{setFilename(e.target.value);setFile(e.target.files[0])}} value={filename} type='file'></input><br />
          <input type='submit'></input>
        </form>
      </header>
      <hr />
      {posts.map(p => (
  <div className="post-container">
    <div className="post-image-container">
      IMAGE GOES HERE
    </div>
    <div className="post-text">
      <font color='maroon'><b>Subject: </b></font>
      <span className='post-subject'>SUBJECT HERE</span><br />
      <span>CONTENT HERE</span>
    </div>
  </div>
))}

    </div>
  );
}

export default Guestbook;
