import React, { useState, useEffect } from 'react';
import './guestbook.css';

function Guestbook() {
  const [posts, setPosts] = useState([])

  useEffect(() => {
    window.scrollTo(0, 0)
    window.scrollTo = function () {};
  }, [])

  return (
    <div id="chan" style={{overflow: "scroll" }}>
      <header style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
        <h1 id="title">33chan</h1>
        <p style={{width: '400px', textAlign: 'center'}}><i>My anonymous imageboard guestbook.</i></p>
        <form onSubmit={e=>{
            e.preventDefault();
            console.log(e.target.name.value)
            return false;
          }} style={{maxWidth: "fit-content"}}>
          <input name='name' placeholder='Your name...' type="text"></input><br />
          <input name='subject' placeholder='Subject...' type="text"></input><br />
          <textarea name='content' placeholder='Say hi!' rows={5} cols={28}></textarea><br />
          <input name='file' type='file'></input><br />
          <input type='submit'></input>
        </form>
      </header>
      <hr />
      {posts.map(p => <div>
        <img src={p.image}></img>
        <div id='post-container'>
          <div id='post-subject'>{p.subject}</div>
          <div id='post-date'>{p.date}</div>
          <div id='post-content'>{p.content}</div>
        </div>
      </div>)}
    </div>
  );
}

export default Guestbook;
