import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './guestbook.css';

function Guestbook() {
  const [posts, setPosts] = useState([])
  const [name, setName] = useState('')
  const [filename, setFilename] = useState('');
  const [file, setFile] = useState(null)
  const [subject, setSubject] = useState('')
  const [content, setContent] = useState('')

  useEffect(() => {
    window.scrollTo(0, 0)
    window.scrollTo = function () {};
  }, [])


  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3001');

    ws.onopen = () => {
      console.log('WebSocket Connected');
      ws.send('Hello Server!');
    };

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      setPosts([...posts, message.data]);
    };

    ws.onerror = (error) => {
      console.log('WebSocket Error: ', error);
    };

    ws.onclose = () => {
      console.log('WebSocket Disconnected');
    };

    return () => {
      ws.close();
    };
  }, []);

  return (
    <div id="chan" style={{overflow: "scroll" }}>
      <header style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
        <h1 id="title">33chan</h1>
        <p style={{width: '400px', textAlign: 'center'}}><i>An anonymous imageboard.</i></p>
        <form onSubmit={async e=>{
            // upload to backend...
            e.preventDefault();
            const formData = new FormData();
            formData.append('username', name);
            formData.append('subject', subject);
            formData.append('content', content);
            if (file) {
              formData.append('file', file);
            }

            try {
              const response = await axios.post('http://localhost:3001/posts', formData, {
                headers: {
                  'Content-Type': 'multipart/form-data',
                },
              });
              //setPosts(posts.concat({ name, subject, content, file: file.name }));
              setName('');
              setSubject('');
              setContent('');
              setFilename('');
              setFile(null);
              alert('Post created successfully!');
            } catch (error) {
              console.error('Error uploading post:', error);
              alert('Error uploading post!');
            }
            return false;
          }} style={{maxWidth: "fit-content"}}>
          <input name='name' onChange={e=>setName(e.target.value)} value={name} placeholder='Your name...' type="text"></input><br />
          <input name='subject' onChange={e=>setSubject(e.target.value)} value={subject} placeholder='Subject...' type="text"></input><br />
          <textarea name='content' onChange={e=>setContent(e.target.value)} value={content} placeholder='Say hi!' rows={5} cols={28}></textarea><br />
          <input name='file' onChange={e=>{setFilename(e.target.value);setFile(e.target.files[0])}} value={filename} type='file'></input><br />
          <input type='submit'></input>
        </form>
      </header>
      <hr />
      {posts.map(p => <div>
        <img src={p.file}></img>
        <div id='post-container'>
          <div id='post-subject'>{p.subject}</div>
          <div id='post-date'>{p.date}</div>
          <div id='post-content'>{p.content}</div>
        </div>
      </div>)}
    </div>
  );
}

export default Guestbook;
